    private void jTable2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable2MouseClicked
                                
    int row = jTable2.getSelectedRow();

    if (row >= 0) {
        String Id = jTable2.getValueAt(row, 0).toString();
        String code = jTable2.getValueAt(row, 1).toString();

        SharedSelection.selectedSubjectId = Integer.parseInt(Id);
        SharedSelection.selectedSubjectCode = code;

        // Optional: set your local fields so SubjectsForm still works normally
        SUBid.setText(Id);
        SUBcode.setText(code);
        SUBdesc.setText(jTable2.getValueAt(row, 2).toString());
        SUBUNITS.setText(jTable2.getValueAt(row, 3).toString());
        SUBsched.setText(jTable2.getValueAt(row, 4).toString());
    }
    
    loadClassList(Integer.valueOf(SUBid.getText()));
    }//GEN-LAST:event_jTable2MouseClicked

