    private void EditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EditActionPerformed
      try {
        int id = Integer.valueOf(SUBid.getText());
        String code = SUBcode.getText();
        String desc = SUBdesc.getText();
        int units = Integer.valueOf(SUBUNITS.getText());
        String sched = SUBsched.getText();
        System.out.println("Updating Record with ID: " + id);
        a.UpdateSubjectRecord(id, code, desc, units, sched);
        loadRecord();
      } catch (NumberFormatException e) {
          e.printStackTrace();
      }
    }//GEN-LAST:event_EditActionPerformed

